import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { Ticket } from '../models/ticket.model';

@Injectable({
  providedIn: 'root'
})
export class TicketService {
  // Feltételezzük, hogy az environment.apiUrl már tartalmazza az '/api' előtagot
  private apiUrl = `${environment.apiUrl}/tickets`;

  constructor(private http: HttpClient) {}

  /**
   * Jegy vásárlása
   * @param eventId Esemény azonosító
   * @param categoryId A jegy kategória azonosítója
   * @param quantity Megvásárolni kívánt jegyek száma
   */
  purchaseTicket(eventId: string, categoryId: string, quantity: number): Observable<Ticket> {
    // Használjuk a már meglévő apiUrl változót a konzisztencia érdekében
    console.log('Jegyvásárlási kérés küldése:', `${this.apiUrl}`, { eventId, categoryId, quantity });
    return this.http.post<Ticket>(`${this.apiUrl}`, {
      eventId,
      categoryId,
      quantity
    });
  }

  getUserTickets(): Observable<Ticket[]> {
    return this.http.get<Ticket[]>(`${this.apiUrl}/user`);
  }

  cancelTicket(ticketId: string): Observable<void> {
    return this.http.post<void>(`${this.apiUrl}/${ticketId}/cancel`, {});
  }

  getEventTickets(eventId: string): Observable<Ticket[]> {
    return this.http.get<Ticket[]>(`${environment.apiUrl}/tickets/event/${eventId}`);
  }
}
