import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, tap } from 'rxjs';
import { environment } from '../../environments/environment';
import { User } from '../models/user.model';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = `${environment.apiUrl}/auth`;
  private currentUserSubject = new BehaviorSubject<User | null>(null);
  public currentUser$ = this.currentUserSubject.asObservable();

  constructor(private http: HttpClient) {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      this.currentUserSubject.next(JSON.parse(storedUser));
    }
  }

  register(userData: {
    email: string;
    password: string;
    firstName: string;
    lastName: string;
  }): Observable<User> {
    return this.http.post<User>(`${this.apiUrl}/register`, userData)
      .pipe(tap(user => {
        this.currentUserSubject.next(user);
        localStorage.setItem('currentUser', JSON.stringify(user));
      }));
  }

  login(credentials: { email: string; password: string }): Observable<User> {
    return this.http.post<User>(`${this.apiUrl}/login`, credentials).pipe(
      tap(user => {
        // Debug, miért nem történik bejelentkezés
        console.log('Bejelentkezés sikeres, kapott adatok:', user);

        // A kapott felhasználó adatait tároljuk a state-ben
        this.currentUserSubject.next(user);

        // Mindkét kulcs alatt tároljuk a nagyobb kompatibilitás érdekében
        localStorage.setItem('currentUser', JSON.stringify(user));
        localStorage.setItem('user', JSON.stringify(user));

        // Ha van token, azt is tároljuk külön
        if (user && user.token) {
          localStorage.setItem('token', user.token);
        }

        console.log('Felhasználói adatok elmentve a localStorage-ba');
      })
    );
  }

  logout(): void {
    this.currentUserSubject.next(null);
    localStorage.removeItem('currentUser');
  }

  isAuthenticated(): boolean {
    return this.currentUserSubject.value !== null;
  }

  getUserRole(): string | null {
    return this.currentUserSubject.value?.role || null;
  }

  isAdmin(): boolean {
    try {
      // Közvetlenül a localStorage-ban lévő 'user' kulcsot ellenőrizzük, amit valóban látunk a böngészőben
      const userString = localStorage.getItem('user');
      console.log('user kulcs tartalma a localStorage-ban:', userString);

      if (userString) {
        try {
          const userData = JSON.parse(userString);
          console.log('Bejelentkezeti felhasználó adatai:', userData);  

          // Ellenőrizzük, hogy van-e role mező és az 'admin'-e
          if (userData && userData.role) {
            const isAdminUser = userData.role === 'admin';
            console.log(`Felhasználó szerepköre: ${userData.role}, admin jogosultság: ${isAdminUser}`);
            return isAdminUser;
          }
        } catch (e) {
          console.error('Hiba a felhasználói adatok feldolgozása közben:', e);
        }
      } else {
        console.log('Nincs user kulcs a localStorage-ban');
      }

      // Közvetlenül kifejezett kövés, ha a felhasználó email címe admin@admin.com
      const emailBasedCheck = this.getCurrentUser()?.email === 'admin@admin.com';
      if (emailBasedCheck) {
        console.log('Email alapú admin ellenőrzés sikeres!');
        return true;
      }

      return false;
    } catch (error) {
      console.error('Hiba az admin jogosultság ellenőrzése közben:', error);
      return false;
    }
  }

  getCurrentUser(): User | null {
    return this.currentUserSubject.value;
  }
}
