import { Component, OnInit } from '@angular/core';
import { TicketService } from '../../../services/ticket.service';
import { Ticket } from '../../../models/ticket.model';

@Component({
  selector: 'app-ticket-list',
  templateUrl: './ticket-list.component.html',
  styleUrls: ['./ticket-list.component.css']
})
export class TicketListComponent implements OnInit {
  tickets: Ticket[] = [];
  activeTickets: Ticket[] = [];
  cancelledTickets: Ticket[] = [];
  showCancelledTickets = false;
  loading = false;
  error: string | null = null;

  // Fixált ár kategóriák a kirínderéléshez
  categoryPrices = {
    'Álló': 10000,
    'VIP': 15000,
    'Normál': 8000
  };

  constructor(private ticketService: TicketService) {}

  ngOnInit(): void {
    this.loadTickets();
  }

  loadTickets(): void {
    this.loading = true;
    this.error = null;

    this.ticketService.getUserTickets().subscribe({
      next: (tickets: Ticket[]) => {
        console.log('Betöltött jegyek:', tickets);

        // Részletesen kilogoljuk a jegyek adatait a hiba diagnosztikálásához
        if (tickets && tickets.length > 0) {
          tickets.forEach((ticket, index) => {
            console.log(`Jegy #${index + 1}:`, {
              id: ticket._id,
              státusz: ticket.status, // Ez lehet 'active', 'cancelled', vagy akár magyar nyelvű érték is
              esemény: ticket.event ? ticket.event.title : 'Nincs esemény adat',
              kategória: ticket.category,
              kategóriaTípus: typeof ticket.category,
              ár: this.getTicketPrice(ticket)
            });
          });
        } else {
          console.warn('Nincsenek jegyek, vagy üres tömb érkezett!');
        }

        this.tickets = tickets || [];

        // Mivel TypeScript problémák vannak a státusz összehasonlításánál,
        // egyértelműen definiáljuk az aktív és inaktív jegyeket
        this.activeTickets = [];
        this.cancelledTickets = [];

        // Egyesével ellenőrizzük a jegyek státuszát
        tickets.forEach(ticket => {
          if (ticket.status) {
            const statusLower = String(ticket.status).toLowerCase();

            if (statusLower === 'cancelled' || statusLower === 'lemondva') {
              this.cancelledTickets.push(ticket);
            } else {
              // Minden más értéket aktívnak tekintünk (pl. 'active', 'confirmed', stb.)
              this.activeTickets.push(ticket);
            }
          } else {
            // Ha nincs státusz, akkor aktívnak tekintjük
            this.activeTickets.push(ticket);
          }
        });

        console.log('Aktív jegyek:', this.activeTickets.length);
        console.log('Lemondott jegyek:', this.cancelledTickets.length);

        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Hiba történt a jegyek betöltése közben. Kérjük próbálja újra később.';
        this.loading = false;
        console.error('Hiba a jegyek betöltése közben:', err);
      }
    });
  }

  /**
   * Jegy státusz kapcsoló
   */
  toggleCancelledTickets(): void {
    this.showCancelledTickets = !this.showCancelledTickets;
  }

  /**
   * Jegy árának meghatározása
   * Mivel a backend csak string-et küld a kategóriára (nem objektumot), 
   * ezért itt határozzuk meg az árat a kategória nevéből
   */
  getTicketPrice(ticket: Ticket): number {
    // Ha a ticket.category egy string, akkor a kategória ártéke a fixált árlistából vagy 0
    if (typeof ticket.category === 'string') {
      const categoryName = ticket.category as string;
      return this.categoryPrices[categoryName] || 0;
    }

    // Ha a kategória egy objektum, akkor az eredeti logika szerint járunk el
    if (ticket.category && typeof ticket.category === 'object' && 'price' in ticket.category) {
      return ticket.category.price;
    }

    // Fallback érték, ha semmi sem működik
    return 0;
  }

  cancelTicket(ticketId: string): void {
    if (confirm('Biztosan lemondód ezt a jegyet?')) {
      this.ticketService.cancelTicket(ticketId).subscribe({
        next: () => {
          this.loadTickets();
        },
        error: (err: any) => {
          this.error = 'Hiba történt a jegy lemondása közben. Kérjük próbálja újra később.';
          console.error('Hiba a jegy lemondása közben:', err);
        }
      });
    }
  }
}
