import { forkJoin } from 'rxjs';
import { TicketService } from 'src/app/services/ticket.service';
import { EventService } from 'src/app/services/event.service';
import { Ticket } from 'src/app/models/ticket.model';
import { Event } from 'src/app/models/event.model';

interface CategoryPriceMap {
  [eventId: string]: {
    [categoryName: string]: number
  }
}

@Component({
  selector: 'app-ticket-list',
  templateUrl: './ticket-list.component.html',
  styleUrls: ['./ticket-list.component.css']
})
export class TicketListComponent implements OnInit {
  tickets: Ticket[] = [];
  activeTickets: Ticket[] = [];
  cancelledTickets: Ticket[] = [];
  showCancelled = false;
  loading = false;
  error: string | null = null;

  // Események jegykategória árainak cache-elése
  events: Event[] = [];
  categoryPrices: CategoryPriceMap = {};

  constructor(
    private ticketService: TicketService,
    private eventService: EventService
  ) {}

  /**
   * Segédmetódus a kategórianevének meghatározására. A template-ben használjuk
   * a typeof operátor használata helyett.
   */
  getCategoryName(ticket: Ticket): string {
    if (ticket.category && typeof ticket.category === 'object') {
      const categoryObj = ticket.category as any;
      return categoryObj.name || 'Ismeretlen kategória';
    }
    // Ha a category egy string
    return typeof ticket.category === 'string' ? ticket.category : 'Ismeretlen kategória';
  }

  ngOnInit(): void {
    this.loadTickets();
  }

  loadTickets(): void {
    this.loading = true;
    this.error = null;

    // Első lépésben betöltjük az összes eseményt az árak miatt
    this.eventService.getEvents().subscribe({
      next: (events) => {
        // Eltároljuk az összes eseményt
        this.events = events;

        // Felkonstruljuk a kategória ár térképet az eseményekből
        this.events.forEach(event => {
          if (!this.categoryPrices[event._id]) {
            this.categoryPrices[event._id] = {};
          }

          // Minden kategória árát eltároljuk az eseményhez
          event.ticketCategories.forEach(category => {
            this.categoryPrices[event._id][category.name] = category.price;
            console.log(`Árakat cache-elünk: ${event.title}, ${category.name}: ${category.price} Ft`);
          });
          if (ticket.status) {
            const statusLower = String(ticket.status).toLowerCase();

            if (statusLower === 'cancelled' || statusLower === 'lemondva') {
              this.cancelledTickets.push(ticket);
            } else {
              // Minden más értéket aktívnak tekintünk (pl. 'active', 'confirmed', stb.)
              this.activeTickets.push(ticket);
            }
          } else {
            // Ha nincs státusz, akkor aktívnak tekintjük
            this.activeTickets.push(ticket);
          }
        });

        console.log('Aktív jegyek:', this.activeTickets.length);
        console.log('Lemondott jegyek:', this.cancelledTickets.length);

        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Hiba történt a jegyek betöltése közben. Kérjük próbálja újra később.';
        this.loading = false;
        console.error('Hiba a jegyek betöltése közben:', err);
      }
    });
  }

  /**
   * Jegy státusz kapcsoló
   */
  toggleCancelledTickets(): void {
    this.showCancelledTickets = !this.showCancelledTickets;
  }

  /**
   * Jegy árának meghatározása
   * Mivel a backend csak string-et küld a kategóriára (nem objektumot), 
   * ezért itt határozzuk meg az árat a kategória nevéből
   */
  /**
   * Kiszámolja a jegy árát a backend adatok alapján.
   * Kizárólag a backend által küldött adatokra támaszkodik, nincs hardkoded érték.
   */
  getTicketPrice(ticket: Ticket): number {
    // A ticket.category lehet objektum vagy string is
    if (!ticket.category) {
      console.log('Nincs kategoria információ a jegyhez');
      return 0;
    }

    // Ha a ticket.category egy objektum, próbáljuk az árat kinyerni belőle
    if (typeof ticket.category === 'object') {
      const categoryObj = ticket.category as any;

      // Ha van price mező, akkor azt használjuk (ez a legjobb eset)
      if (typeof categoryObj.price === 'number') {
        console.log(`Kategoria ár a jegy objektumból: ${categoryObj.price} Ft`);
        return categoryObj.price;
      }

      // Ha nincs price mező, de van name mező, akkor kiírjuk ezt a problémát
      if (categoryObj.name) {
        console.log(`Hiba: A "${categoryObj.name}" nevű kategóriának nincs price mezője`);
      } else {
        console.log('Hiba: A kategoria objektumnak nincs name mezője');
      }

      // Egyéb lehetőségek ellenőrzése
      // Log-oljuk a kategória objektum mezőit
      console.log('Kategória objektum mezői:');
      for (const key in categoryObj) {
        console.log(`- ${key}: ${categoryObj[key]}`);

        // Ha találunk valamilyen árnak tűnő mezőt, azt is használhatjuk
        if (['price', 'cost', 'amount', 'value'].includes(key.toLowerCase()) && 
            typeof categoryObj[key] === 'number') {
          console.log(`Alternatív ármező találat: ${key} = ${categoryObj[key]} Ft`);
          return categoryObj[key];
        }
      }
    }

    // Ha string típusú a category (de ez ritka eset), akkor megírjuk ezt a problémát
    if (typeof ticket.category === 'string') {
      console.log(`A jegyhez csak kategória név érkezett ár nélkül: "${ticket.category}". `);
    }

    // Ha nem találtunk árat, visszaadunk egy null/0 értéket
    console.warn('Nem sikerült árat meghatározni a jegyhez.');
    return 0;
  }

  cancelTicket(ticketId: string): void {
    if (confirm('Biztosan lemondód ezt a jegyet?')) {
      this.ticketService.cancelTicket(ticketId).subscribe({
        next: () => {
          this.loadTickets();
        },
        error: (err: any) => {
          this.error = 'Hiba történt a jegy lemondása közben. Kérjük próbálja újra később.';
          console.error('Hiba a jegy lemondása közben:', err);
        }
      });
    }
  }
}
