import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { Ticket } from '../models/ticket.model';

@Injectable({
  providedIn: 'root'
})
export class TicketService {
  // Feltételezzük, hogy az environment.apiUrl már tartalmazza az '/api' előtagot
  private apiUrl = `${environment.apiUrl}/tickets`;

  constructor(private http: HttpClient) {}

  /**
   * Jegy vásárlása
   * @param eventId Esemény azonosító
   * @param categoryId A jegy kategória azonosítója
   * @param quantity Megvásárolni kívánt jegyek száma
   */
  purchaseTicket(eventId: string, categoryId: string, quantity: number): Observable<Ticket> {
    // Minden lehetséges formátumban elküljük az adatokat, hogy megtaláljuk, mit vár a backend
    const requestPayload = {
      eventId: eventId,
      categoryId: categoryId,
      quantity: quantity,
      event: eventId,        // alternatív mezőnév
      category: categoryId,  // alternatív mezőnév
      count: quantity        // alternatív mezőnév
    };

    console.log('Jegyvásárlási kérés küldése:', `${this.apiUrl}/purchase`, requestPayload);
    return this.http.post<Ticket>(`${this.apiUrl}/purchase`, requestPayload);
  }

  getUserTickets(): Observable<Ticket[]> {
    // Debug információk a kérésről
    console.log('Felhasználói jegyek lekérése:', `${this.apiUrl}/user`);

    // A backend API végpont valószínűleg: /api/tickets/my-tickets
    // Ha a /user végpont 500-as hibát ad, próbáljuk ezt használni helyette
    return this.http.get<Ticket[]>(`${this.apiUrl}/my-tickets`);
  }

  cancelTicket(ticketId: string): Observable<void> {
    return this.http.post<void>(`${this.apiUrl}/${ticketId}/cancel`, {});
  }

  getEventTickets(eventId: string): Observable<Ticket[]> {
    // Használjuk a már meglévő apiUrl változót a konzisztencia érdekében
    console.log('Eseményjegyek lekérése:', `${this.apiUrl}/event/${eventId}`);
    return this.http.get<Ticket[]>(`${this.apiUrl}/event/${eventId}`);
  }
}
