import { Component, OnInit } from '@angular/core';
import { EventService } from '../../../services/event.service';
import { Event } from '../../../models/event.model';
import { AuthService } from '../../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-event-list',
  templateUrl: './event-list.component.html',
  styleUrls: ['./event-list.component.css']
})
export class EventListComponent implements OnInit {
  events: Event[] = [];
  loading = true;
  error: string | null = null;
  isAuthenticated = false;
  isAdmin = false;
  // Diagnosztikai adatok
  currentUserEmail: string = '';
  currentUserRole: string = '';

  constructor(
    private eventService: EventService,
    public authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadEvents();

    // Egyértelmű debug információk
    console.log('EventListComponent betöltődött');

    // Figyelünk a bejelentkezett felhasználó változására
    this.authService.currentUser$.subscribe(user => {
      console.log('Felhasználó frissítve:', user);

      // Beállítjuk a hitelesítési állapotot
      this.isAuthenticated = !!user;

      // Diagnosztikai adatok beállítása
      if (user) {
        this.currentUserEmail = user.email || '';
        this.currentUserRole = user.role || '';
        console.log('Felhasználó szerepköre:', user.role);

        // Debug célja: kiírjuk a teljes user objektumot
        console.log('Teljes felhasználói objektum:', JSON.stringify(user));
      } else {
        this.currentUserEmail = '';
        this.currentUserRole = '';
      }

      // Admin jogosultságok ellenőrzése a szolgáltatáson keresztül
      this.isAdmin = this.authService.isAdmin();
      console.log('Admin jogosultság állapota:', this.isAdmin);
    });
  }

  loadEvents(): void {
    this.loading = true;
    this.eventService.getEvents().subscribe({
      next: (events) => {
        this.events = events;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Hiba történt az események betöltése közben.';
        this.loading = false;
        console.error('Error loading events:', err);
      }
    });
  }

  viewDetails(eventId: string): void {
    this.router.navigate(['/events', eventId]);
  }

  purchaseTicket(eventId: string): void {
    this.router.navigate(['/events', eventId, 'purchase']);
  }

  editEvent(eventId: string): void {
    this.router.navigate(['/events', eventId, 'edit']);
  }

  // Admin állapot frissítése és ellenőrzése
  checkAdminStatus(): void {
    // Kéziközel adminnak állítjuk be, hogy lássuk a gombot tesztelés céljából
    this.isAdmin = true;
    console.log('Admin jogosultság kézzel beállítva:', this.isAdmin);
    console.log('Kérlek kattints most az Új esemény gombra!');

    // Diagnosztikai célla kiírjuk a felhasználói adatokat és szerepköröket
    const currentUser = this.authService.getCurrentUser();
    console.log('Felhasználói objektum:', currentUser);
    if (currentUser) {
      console.log('Szerepkör:', currentUser.role);
      console.log('Szerepkör típusa:', typeof currentUser.role);
    }
  }

  createEvent(): void {
    console.log('createEvent() meghívásra került');
    // Közvetlenül a browser konzolra is kiírjuk a debug üzenetet, ha van
    if (typeof window !== 'undefined' && window.console) {
      window.console.log('%c EVENT CRÉATION ATTEMPT ', 'background: #ff0000; color: #ffffff');
    }

    // Debug: próbáljuk meg abszolút URL-lel is
    this.router.navigate(['/events/create'], { skipLocationChange: false })
      .then(() => {
        console.log('Navigáció sikerült');
      })
      .catch(error => {
        console.error('Navigációs hiba:', error);
      });
  }
}
