import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, catchError, throwError, forkJoin } from 'rxjs';
import { map } from 'rxjs/operators';
import { environment } from '../../environments/environment';
import { Ticket } from '../models/ticket.model';
import { Event } from '../models/event.model';
import { EventService } from './event.service';

@Injectable({
  providedIn: 'root'
})
export class TicketService {
  // Feltételezzük, hogy az environment.apiUrl már tartalmazza az '/api' előtagot
  // Közvetlenül használjuk az environment.apiUrl-t a következetesség érdekében

  constructor(private http: HttpClient) {}

  /**
   * Jegy vásárlása
   * @param eventId Esemény azonosító
   * @param categoryId A jegy kategória azonosítója
   * @param quantity Megvásárolni kívánt jegyek száma
   */
  /**
   * Jegy vásárlása (egy vagy több)
   * A backend a kategória NEVET várja, nem annak ID-ját!
   * FONTOS! A backendben lévő értékek vizsgálata alapján
   * 1. A status 'active' érték a controllerben, de az adatbázisban 'confirmed' értékek vannak.
   * 2. Többszöri jegy vásárlásnál külön kéréseket kell küldeni.
   */
  purchaseTicket(eventId: string, categoryName: string, quantity: number): Observable<Ticket> {
    // A backend kód megtekintése után látjuk, hogy a backend a kategória NEVÉT várja, nem az azonosítóját!
    // A backend kódrészlet:
    // const ticketCategory = event.ticketCategories.find((cat) => cat.name === category);

    const endpoint = `${environment.apiUrl}/tickets`;

    // A backendnek küldünk egy kérést, ami tartalmazza a mennyiséget is
    // Így a backend meg tudja spórolni a többszörös adatbázis lekérdezést
    const requestPayload = {
      eventId: eventId,         // Az esemény ID-ja
      category: categoryName,   // A kategória neve (nem az ID!)
      quantity: quantity        // A vásárolni kívánt jegyek száma
    };

    // 1. Leőször közvetlenül elküldjük a kérést a backendnek
    console.log(`Jegyvásárlás indul. Esemény: ${eventId}, Kategória: ${categoryName}, Mennyiség: ${quantity}`);
    console.log('Request payload:', JSON.stringify(requestPayload));

    // A backendnek most már egy kérésben küldjük el a teljes jegyvásárlási igényt,
    // hiszen a requestPayload-ban megadtuk a quantity értékét
    console.log('Egyszerűsített jegyvásárlás egy kéréssel, mennyiség:', quantity);

    return this.http.post<Ticket>(endpoint, requestPayload).pipe(
      catchError(error => {
        console.error('Hiba a jegyvásárlás során:', error);
        console.log('Pontos request payload:', requestPayload);
        return this.handleJegyvasarlasError(error);
      })
    );
  }

  /**
   * Jegyvásárláskor keletkezett hibák kezelése
   * Részletes diagnosztikai naplózással
   */
  private handleJegyvasarlasError(error: HttpErrorResponse) {
    console.error('Részletes vásárlási hiba:', {
      status: error.status,
      message: error.message,
      errorBody: error.error
    });

    if (error.status === 500) {
      console.error('500-as szerver hiba történt. Lehetséges problémák:');
      console.error('1. A backend request formátuma és a küldött adatok nem egyeznek');
      console.error('2. Szerverhiba a jegy létrehozása közben');
      console.error('3. Adatbázishiba vagy az esemény/kategória nem található');
    }

    if (error.error && error.error.errors && Array.isArray(error.error.errors)) {
      console.log('A szerver által jelentett hibák:');
      error.error.errors.forEach((err: any, index: number) => {
        console.log(`Hiba #${index + 1}:`, err);
      });
    }

    return throwError(() => error);
  }

  getUserTickets(): Observable<Ticket[]> {
    // Alternatív végpontokat próbálunk, mert az eredeti 500-as hibát ad
    // 1. Végpont: /api/tickets/my-tickets
    const endpoint = `${environment.apiUrl}/tickets/my-tickets`;
    console.log('Felhasználói jegyek lekérése:', endpoint);

    return this.http.get<Ticket[]>(endpoint);
  }

  cancelTicket(ticketId: string): Observable<void> {
    // A backend route-okból látszik a helyes API végpont: POST /:id/cancel
    const endpoint = `${environment.apiUrl}/tickets/${ticketId}/cancel`;
    console.log('Jegy lemondása (javított végpont):', endpoint);

    // A backendhez nem kell külön payload, mert az ID már az URL-ben van
    return this.http.post<void>(endpoint, {});
  }

  getEventTickets(eventId: string): Observable<Ticket[]> {
    const endpoint = `${environment.apiUrl}/tickets/event/${eventId}`;
    console.log('Eseményjegyek lekérése:', endpoint);
    return this.http.get<Ticket[]>(endpoint);
  }

  /**
   * Összes jegy lekérése admin célokra
   * Csak admin jogosultsággal hívható
   * @returns Observable az összes jeggyel
   */
  getAllTickets(): Observable<Ticket[]> {
    // Csak a valós jegyeket kérjük le, tesztadatok nélkül
    // Ez a megoldás admin felhasználóknak csak a valós jegyeket fogja megjeleníteni
    console.log('Adminnak valós jegyek lekérése (my-tickets API)...');
    return this.getUserTickets().pipe(
      catchError(error => {
        console.error('Hiba az összes jegy lekérése közben:', error);
        return throwError(() => error);
      })
    );
  }

  // Segédfüggvény az EventService-hez való hozzáféréshez
  // Injectálási körkörös függőség elkerülése miatt
  private eventService: EventService | null = null;
  getEventService(): EventService {
    if (!this.eventService) {
      this.eventService = new EventService(this.http);
    }
    return this.eventService;
  }
}
