import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, tap } from 'rxjs';
import { environment } from '../../environments/environment';
import { User } from '../models/user.model';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = `${environment.apiUrl}/auth`;
  private currentUserSubject = new BehaviorSubject<User | null>(null);
  public currentUser$ = this.currentUserSubject.asObservable();

  constructor(private http: HttpClient) {
    const storedUser = localStorage.getItem('currentUser');
    if (storedUser) {
      this.currentUserSubject.next(JSON.parse(storedUser));
    }
  }

  register(userData: {
    email: string;
    password: string;
    firstName: string;
    lastName: string;
  }): Observable<User> {
    return this.http.post<User>(`${this.apiUrl}/register`, userData)
      .pipe(tap(user => {
        this.currentUserSubject.next(user);
        localStorage.setItem('currentUser', JSON.stringify(user));
      }));
  }

  login(credentials: { email: string; password: string }): Observable<User> {
    return this.http.post<User>(`${this.apiUrl}/login`, credentials).pipe(
      tap(user => {
        this.currentUserSubject.next(user);
        localStorage.setItem('currentUser', JSON.stringify(user));
      })
    );
  }

  logout(): void {
    this.currentUserSubject.next(null);
    localStorage.removeItem('currentUser');
  }

  isAuthenticated(): boolean {
    return this.currentUserSubject.value !== null;
  }

  getUserRole(): string | null {
    return this.currentUserSubject.value?.role || null;
  }

  isAdmin(): boolean {
    // Egyszerűsített, robusztus implementáció
    try {
      // Próbáljuk meg közvetlenül a localStorage-ból kinyerni a felhasználói adatokat
      const storedUserString = localStorage.getItem('currentUser');

      if (!storedUserString) {
        console.log('Nincs felhasználói adat a localStorage-ban');
        return false;
      }

      // Próbáljuk meg feltörölni a JSON-t
      const storedUser = JSON.parse(storedUserString);
      console.log('Felhasználói adatok a localStorage-ból:', storedUser);

      // Ha nincs role tulajdonság, akkor nem admin
      if (!storedUser || !storedUser.role) {
        console.log('Nincs role tulajdonság a felhasználói objektumban');
        return false;
      }

      // Kisbetűsítve és trim-elve ellenőrizzük az 'admin' szerepkört
      const isAdmin = String(storedUser.role).trim().toLowerCase() === 'admin';
      console.log(`A felhasználó szerepköre: ${storedUser.role}, admin jogosultsága: ${isAdmin}`);
      return isAdmin;
    } catch (error) {
      console.error('Hiba az admin jogosultság ellenőrzése közben:', error);
      return false;
    }
  }

  getCurrentUser(): User | null {
    return this.currentUserSubject.value;
  }
}
