import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import { Observable, catchError, throwError } from 'rxjs';
import { environment } from '../../environments/environment';
import { Ticket } from '../models/ticket.model';

@Injectable({
  providedIn: 'root'
})
export class TicketService {
  // Feltételezzük, hogy az environment.apiUrl már tartalmazza az '/api' előtagot
  // Közvetlenül használjuk az environment.apiUrl-t a következetesség érdekében

  constructor(private http: HttpClient) {}

  /**
   * Jegy vásárlása
   * @param eventId Esemény azonosító
   * @param categoryId A jegy kategória azonosítója
   * @param quantity Megvásárolni kívánt jegyek száma
   */
  /**
   * Jegyvásárlás - többszörös jegyvásárlás támogatással
   * @param eventId Az esemény azonosítója
   * @param categoryName A kategória NEVE (nem ID-ja)
   * @param quantity Jegyek száma (szám típusú)
   * @returns Egy vagy több jegyet Observable-ben
   */
  purchaseTicket(eventId: string, categoryName: string, quantity: number): Observable<Ticket | Ticket[]> {
    // Biztosítsuk, hogy szám típusa van a mennyiségnek
    const numericQuantity = parseInt(String(quantity), 10);
    if (isNaN(numericQuantity) || numericQuantity < 1) {
      console.error('Hibás jegy darabszám:', quantity);
      return throwError(() => new Error('Hibás jegy darabszám'));
    }

    // Az API végpont és kérés előkészítése
    const endpoint = `${environment.apiUrl}/tickets`;

    // HTTP kérés elkészítése explicit fejléc beállításokkal
    const headers = new HttpHeaders({
      'Content-Type': 'application/json'
    });

    // A json payload elkészítése - a legfontosabb rész!
    const requestBody = JSON.stringify({
      eventId,
      category: categoryName,
      quantity: numericQuantity
    });

    console.log('Jegyvásárlás indítása:');
    console.log('- URL:', endpoint);
    console.log('- Headers:', headers);
    console.log('- Body:', requestBody);
    console.log('- Darabszám típusa:', typeof numericQuantity);
    console.log('- Minden adat:', {
      eventId,
      categoryName,
      requestedQuantity: quantity,
      processedQuantity: numericQuantity,
      requestType: typeof requestBody
    });

    // A kérést most már a requestBody változóval küldenménk, de az tartalmazza a JSON.stringify-t
    // Helyette közvetlenül az objektumot küldtük be
    return this.http.post<Ticket | Ticket[]>(endpoint, {
      eventId,
      category: categoryName,
      quantity: numericQuantity
    }, { headers })
      .pipe(
        catchError((error: HttpErrorResponse) => {
          console.error('Részletes vásárlási hiba:', {
            status: error.status,
            message: error.message,
            errorBody: error.error
          });

          if (error.error && error.error.errors && Array.isArray(error.error.errors)) {
            console.log('A szerver által jelentett hibák:');
            error.error.errors.forEach((err: any, index: number) => {
              console.log(`Hiba #${index + 1}:`, err);
            });
          }

          return throwError(() => error);
        })
      );
  }

  getUserTickets(): Observable<Ticket[]> {
    // Alternatív végpontokat próbálunk, mert az eredeti 500-as hibát ad
    // 1. Végpont: /api/tickets/my-tickets
    const endpoint = `${environment.apiUrl}/tickets/my-tickets`;
    console.log('Felhasználói jegyek lekérése:', endpoint);

    return this.http.get<Ticket[]>(endpoint);
  }

  cancelTicket(ticketId: string): Observable<void> {
    // A backend route-okból látszik a helyes API végpont: POST /:id/cancel
    const endpoint = `${environment.apiUrl}/tickets/${ticketId}/cancel`;
    console.log('Jegy lemondása (javított végpont):', endpoint);

    // A backendhez nem kell külön payload, mert az ID már az URL-ben van
    return this.http.post<void>(endpoint, {});
  }

  getEventTickets(eventId: string): Observable<Ticket[]> {
    const endpoint = `${environment.apiUrl}/tickets/event/${eventId}`;
    console.log('Eseményjegyek lekérése:', endpoint);
    return this.http.get<Ticket[]>(endpoint);
  }
}
