import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, map, tap, catchError, throwError } from 'rxjs';
import { environment } from '../../../../environments/environment';
import { ErrorService } from '../../../core/services/error.service';
import { LoadingService } from '../../../core/services/loading.service';

export interface User {
  id: string;
  name: string;
  email: string;
  role: 'user' | 'admin';
}

export interface AuthResponse {
  user: User;
  token: string;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = `${environment.apiUrl}/auth`;
  private currentUserSubject = new BehaviorSubject<User | null>(null);
  currentUser$ = this.currentUserSubject.asObservable();
  isAuthenticated$ = this.currentUser$.pipe(map(user => !!user));
  isAdmin$ = this.currentUser$.pipe(map(user => user?.role === 'admin'));

  constructor(
    private http: HttpClient,
    private errorService: ErrorService,
    private loadingService: LoadingService
  ) {
    const token = localStorage.getItem('token');
    if (token) {
      this.loadUserFromToken(token);
    }
  }

  login(email: string, password: string): Observable<AuthResponse> {
    this.loadingService.setLoading(true);
    return this.http.post<AuthResponse>(`${this.apiUrl}/login`, { email, password }).pipe(
      tap((response: AuthResponse) => {
        localStorage.setItem('token', response.token);
        this.currentUserSubject.next(response.user);
        this.loadingService.setLoading(false);
      }),
      catchError((error: Error) => {
        this.errorService.handleError(error);
        this.loadingService.setLoading(false);
        return throwError(() => error);
      })
    );
  }

  register(name: string, email: string, password: string): Observable<AuthResponse> {
    this.loadingService.setLoading(true);
    return this.http.post<AuthResponse>(`${this.apiUrl}/register`, { name, email, password }).pipe(
      tap((response: AuthResponse) => {
        localStorage.setItem('token', response.token);
        this.currentUserSubject.next(response.user);
        this.loadingService.setLoading(false);
      }),
      catchError((error: Error) => {
        this.errorService.handleError(error);
        this.loadingService.setLoading(false);
        return throwError(() => error);
      })
    );
  }

  logout(): void {
    localStorage.removeItem('token');
    this.currentUserSubject.next(null);
  }

  private loadUserFromToken(token: string): void {
    // In a real application, you would decode the JWT token to get the user info
    // For now, we'll just make a request to get the current user
    this.http.get<User>(`${this.apiUrl}/me`).subscribe({
      next: (user) => {
        this.currentUserSubject.next(user);
      },
      error: () => {
        localStorage.removeItem('token');
        this.currentUserSubject.next(null);
      }
    });
  }
}
