import { Component, OnInit } from '@angular/core';
import { EventService } from '../../../services/event.service';
import { TicketService } from '../../../services/ticket.service';
import { AuthService } from '../../../services/auth.service';
import { Router } from '@angular/router';
import { Event } from '../../../models/event.model';
import { Ticket } from '../../../models/ticket.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  events: Event[] = [];
  tickets: Ticket[] = [];
  loading = true;
  error: string | null = null;

  // Statisztikai adatok
  totalEvents = 0;
  totalTickets = 0;
  totalSales = 0;
  totalActiveTickets = 0;
  totalCancelledTickets = 0;
  eventsByMonth: any[] = [];
  ticketsByCategory: any[] = [];

  // Hónapi események száma
  monthlyEventCounts: number[] = [];

  // Jegy kategóriák és statisztikák
  ticketCategories: { name: string, count: number, revenue: number }[] = [];

  constructor(
    private eventService: EventService,
    private ticketService: TicketService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit(): void {
    if (!this.authService.isAdmin()) {
      this.router.navigate(['/events']);
      return;
    }

    this.loadDashboardData();
  }

  loadDashboardData(): void {
    this.loading = true;

    // Események betöltése
    this.eventService.getEvents().subscribe({
      next: (events) => {
        this.events = events;
        this.totalEvents = events.length;
        this.calculateEventsByMonth();

        // Jegyek betöltése események után
        this.loadTicketData();
      },
      error: (err) => {
        this.error = 'Hiba történt az események betöltése közben.';
        this.loading = false;
        console.error('Error loading events:', err);
      }
    });
  }

  loadTicketData(): void {
    this.ticketService.getAllTickets().subscribe({
      next: (tickets: Ticket[]) => {
        if (!tickets || !Array.isArray(tickets)) {
          this.error = 'Hiba történt a jegyek betöltése közben: Érvénytelen adatformátum';
          this.loading = false;
          return;
        }

        this.tickets = tickets;
        this.totalTickets = tickets.length;
        // A Ticket modellben a státusz típusai: 'pending' | 'confirmed' | 'cancelled'
        this.totalActiveTickets = tickets.filter((t: Ticket) => t.status === 'confirmed' || t.status === 'pending').length;
        this.totalCancelledTickets = tickets.filter((t: Ticket) => t.status === 'cancelled').length;

        // Számítsuk ki az összbevételt
        this.totalSales = tickets
          .filter((t: Ticket) => t.status !== 'cancelled')
          .reduce((sum: number, ticket: Ticket) => {
            // Biztonságos ár konverzió - a price érték lehet undefined vagy number típus
            const price = typeof ticket.price === 'number' ? ticket.price : 0;
            return sum + price;
          }, 0);

        this.calculateTicketsByCategory();

        this.loading = false;
        this.error = null; // Töröljük a hibát, ha sikeres a betöltés
      },
      error: (err: any) => {
        this.error = 'Hiba történt a jegyek betöltése közben.';
        this.loading = false;
        console.error('Error loading tickets:', err);
      }
    });
  }

  /**
   * Események havi eloszlásának számítása
   */
  calculateEventsByMonth(): void {
    this.monthlyEventCounts = Array(12).fill(0);

    this.events.forEach(event => {
      if (event.date) {
        const month = new Date(event.date).getMonth();
        this.monthlyEventCounts[month]++;
      }
    });
  }

  /**
   * Jegyek kategoriánkénti eloszlásának számítása
   */
  calculateTicketsByCategory(): void {
    // Jegyek és bevételek kategóriánként
    const categoryData = new Map<string, { count: number, revenue: number }>();

    this.tickets.forEach((ticket: Ticket) => {
      // Ha nincs kategória, "Ismeretlen" kategóriába soroljuk
      const category = ticket.category || 'Ismeretlen';

      if (ticket.status !== 'cancelled') {
        const categoryInfo = categoryData.get(category) || { count: 0, revenue: 0 };
        categoryInfo.count += 1;

        // Biztonságos ár konverzió
        let ticketPrice = 0;
        if (ticket.price !== undefined && ticket.price !== null) {
          ticketPrice = typeof ticket.price === 'number' ? ticket.price : 0;
        }

        categoryInfo.revenue += ticketPrice;
        categoryData.set(category, categoryInfo);
      }
    });

    this.ticketCategories = Array.from(categoryData.entries())
      .map(([name, data]) => ({
        name,
        count: data.count,
        revenue: data.revenue
      }))
      // Rendezzük csökkenő sorrend szerint a jegyek száma alapján
      .sort((a, b) => b.count - a.count);
  }

  /**
   * Adott hónapban lévő események száma
   */
  getEventCountByMonth(monthIndex: number): number {
    return this.monthlyEventCounts[monthIndex] || 0;
  }

  /**
   * Jegy kategóriák listájának lekérése
   */
  getTicketCategories(): { name: string, count: number, revenue: number }[] {
    return this.ticketCategories;
  }

  /**
   * Dátum formázása
   */
  formatDate(dateString: string): string {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleString('hu-HU', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit' });
  }

  /**
   * Navigáció az események oldalra
   */
  navigateToEvents(): void {
    this.router.navigate(['/events']);
  }

  /**
   * Esemény szerkesztése
   */
  editEvent(eventId: string): void {
    this.router.navigate(['/events', eventId, 'edit']);
  }

  /**
   * Esemény megtekintése
   */
  viewEvent(eventId: string): void {
    this.router.navigate(['/events', eventId]);
  }

  /**
   * Kiszámolja egy eseményhez elérhető összes jegy számát
   * @param event Az esemény, amihez az elérhető jegyeket számoljuk
   * @returns Az elérhető jegyek száma
   */
  getAvailableTickets(event: Event): number {
    if (!event || !event.ticketCategories) return 0;

    // Összegezzük a kategóriánként elérhető helyeket
    return event.ticketCategories.reduce((sum, category) => {
      return sum + (category.availableSeats || 0);
    }, 0);
  }
}
<div class="container-fluid mt-4">
  <div class="row">
    <div class="col-12">
      <h2 class="dashboard-title">
        <i class="fas fa-chart-line me-2"></i>Admin Vezérlőpult
      </h2>

      <!-- Töltési állapot -->
      <div *ngIf="loading" class="text-center p-5">
        <div class="spinner-border text-primary" role="status">
          <span class="visually-hidden">Loading...</span>
        </div>
        <p class="mt-3">Adatok betöltése...</p>
      </div>

      <!-- Hiba üzenet -->
      <div *ngIf="error" class="alert alert-danger">
        {{ error }}
      </div>
    </div>
  </div>

  <!-- Statisztikai kártyák -->
  <div *ngIf="!loading" class="row mt-4">
    <div class="col-xl-3 col-md-6 mb-4">
      <div class="card stat-card bg-primary text-white h-100">
        <div class="card-body">
          <div class="row align-items-center">
            <div class="col-3">
              <i class="fas fa-calendar fa-3x"></i>
            </div>
            <div class="col-9 text-end">
              <h3>{{ totalEvents }}</h3>
              <div>Esemény</div>
            </div>
          </div>
        </div>

      </div>
    </div>

    <div class="col-xl-3 col-md-6 mb-4">
      <div class="card stat-card bg-success text-white h-100">
        <div class="card-body">
          <div class="row align-items-center">
            <div class="col-3">
              <i class="fas fa-ticket-alt fa-3x"></i>
            </div>
            <div class="col-9 text-end">
              <h3>{{ totalActiveTickets }}</h3>
              <div>Megvásárolt jegyek</div>
            </div>
          </div>
        </div>

      </div>
    </div>

    <div class="col-xl-3 col-md-6 mb-4">
      <div class="card stat-card bg-info text-white h-100">
        <div class="card-body">
          <div class="row align-items-center">
            <div class="col-3">
              <i class="fas fa-money-bill-wave fa-3x"></i>
            </div>
            <div class="col-9 text-end">
              <h3>{{ totalSales }} Ft</h3>
              <div>Összes bevétel</div>
            </div>
          </div>
        </div>

      </div>
    </div>


  </div>

  <!-- Statisztikai adatok egyszerűsített megjelenítése -->
  <div *ngIf="!loading" class="row mt-4">
    <div class="col-xl-6 mb-4">
      <div class="card chart-card h-100">
        <div class="card-header">
          <h5 class="card-title">
            <i class="fas fa-chart-bar me-2"></i>Események havi eloszlása
          </h5>
        </div>
        <div class="card-body">
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Hónap</th>
                  <th>Események száma</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let month of ['Január', 'Február', 'Március', 'Április', 'Május', 'Június', 'Július', 'Augusztus', 'Szeptember', 'Október', 'November', 'December']; let i = index">
                  <td>{{ month }}</td>
                  <td>{{ getEventCountByMonth(i) }}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>

    <div class="col-xl-6 mb-4">
      <div class="card h-100">
        <div class="card-header">
          <h5 class="card-title">
            <i class="fas fa-list me-2"></i>Legközelebbi események
          </h5>
        </div>
        <div class="card-body">
          <div class="table-responsive">
            <table class="table table-hover">
              <thead>
                <tr>
                  <th>Esemény</th>
                  <th>Dátum</th>
                  <th>Elérhető jegyek</th>
                  <th>Műveletek</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let event of events.slice(0, 5)">
                  <td>{{ event.title }}</td>
                  <td>{{ formatDate(event.date) }}</td>
                  <td>{{ getAvailableTickets(event) }}</td>
                  <td>
                    <button class="btn btn-sm btn-warning me-1" (click)="editEvent(event._id)">
                      <i class="fas fa-edit"></i>
                    </button>
                    <button class="btn btn-sm btn-info" (click)="viewEvent(event._id)">
                      <i class="fas fa-eye"></i>
                    </button>
                  </td>
                </tr>
                <tr *ngIf="events.length === 0">
                  <td colspan="4" class="text-center">Nincs megjeleníthető esemény</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
        <div class="card-footer d-flex justify-content-end">
          <button class="btn btn-primary" (click)="navigateToEvents()">Összes esemény</button>
        </div>
      </div>
    </div>
  </div>

  <!-- Jegyek kategóriánként -->
  <div *ngIf="!loading" class="row mt-4">
    <div class="col-12 mb-4">
      <div class="card chart-card h-100">
        <div class="card-header">
          <h5 class="card-title">
            <i class="fas fa-chart-pie me-2"></i>Jegyek kategóriánként
          </h5>
        </div>
        <div class="card-body">
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Kategória</th>
                  <th>Jegyek száma</th>
                  <th>Bevétel (Ft)</th>
                </tr>
              </thead>
              <tbody>
                <tr *ngFor="let category of getTicketCategories()">
                  <td>{{ category.name }}</td>
                  <td>{{ category.count }}</td>
                  <td>{{ category.revenue }} Ft</td>
                </tr>
                <tr *ngIf="getTicketCategories().length === 0">
                  <td colspan="3" class="text-center">Nincs megjeleníthető adat</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
