import { Component, OnInit } from '@angular/core';
import { TicketService } from '../../../services/ticket.service';
import { Ticket } from '../../../models/ticket.model';

@Component({
  selector: 'app-ticket-list',
  templateUrl: './ticket-list.component.html',
  styleUrls: ['./ticket-list.component.css']
})
export class TicketListComponent implements OnInit {
  tickets: Ticket[] = [];
  activeTickets: Ticket[] = [];
  cancelledTickets: Ticket[] = [];
  showCancelledTickets = false;
  loading = false;
  error: string | null = null;

  // Ismert kategoriák árai a backendben található értékek alapján
  categoryPriceMap: { [key: string]: number } = {
    'Terasz': 25000,
    'Álló': 10000,
    'VIP': 15000,
    'Normál': 8000
  };

  constructor(private ticketService: TicketService) {}

  /**
   * Segédmetódus a kategórianevének meghatározására. A template-ben használjuk
   * a typeof operátor használata helyett.
   */
  getCategoryName(ticket: Ticket): string {
    if (ticket.category && typeof ticket.category === 'object') {
      const categoryObj = ticket.category as any;
      return categoryObj.name || 'Ismeretlen kategória';
    }
    // Ha a category egy string
    return typeof ticket.category === 'string' ? ticket.category : 'Ismeretlen kategória';
  }

  ngOnInit(): void {
    this.loadTickets();
  }

  loadTickets(): void {
    this.loading = true;
    this.error = null;

    this.ticketService.getUserTickets().subscribe({
      next: (tickets: Ticket[]) => {
        console.log('Betöltött jegyek:', tickets);

        // Részletesen kilogoljuk a jegyek adatait a hiba diagnosztikálásához
        if (tickets && tickets.length > 0) {
          tickets.forEach((ticket, index) => {
            console.log(`Jegy #${index + 1}:`, {
              id: ticket._id,
              státusz: ticket.status, // Ez lehet 'active', 'cancelled', vagy akár magyar nyelvű érték is
              esemény: ticket.event ? ticket.event.title : 'Nincs esemény adat',
              kategória: ticket.category,
              kategóriaTípus: typeof ticket.category,
              ár: this.getTicketPrice(ticket)
            });
          });
        } else {
          console.warn('Nincsenek jegyek, vagy üres tömb érkezett!');
        }

        this.tickets = tickets || [];

        // Mivel TypeScript problémák vannak a státusz összehasonlításánál,
        // egyértelműen definiáljuk az aktív és inaktív jegyeket
        this.activeTickets = [];
        this.cancelledTickets = [];

        // Egyesével ellenőrizzük a jegyek státuszát
        tickets.forEach(ticket => {
          if (ticket.status) {
            const statusLower = String(ticket.status).toLowerCase();

            if (statusLower === 'cancelled' || statusLower === 'lemondva') {
              this.cancelledTickets.push(ticket);
            } else {
              // Minden más értéket aktívnak tekintünk (pl. 'active', 'confirmed', stb.)
              this.activeTickets.push(ticket);
            }
          } else {
            // Ha nincs státusz, akkor aktívnak tekintjük
            this.activeTickets.push(ticket);
          }
        });

        console.log('Aktív jegyek:', this.activeTickets.length);
        console.log('Lemondott jegyek:', this.cancelledTickets.length);

        this.loading = false;
      },
      error: (err: any) => {
        this.error = 'Hiba történt a jegyek betöltése közben. Kérjük próbálja újra később.';
        this.loading = false;
        console.error('Hiba a jegyek betöltése közben:', err);
      }
    });
  }

  /**
   * Jegy státusz kapcsoló
   */
  toggleCancelledTickets(): void {
    this.showCancelledTickets = !this.showCancelledTickets;
  }

  /**
   * Jegy árának meghatározása
   * Mivel a backend csak string-et küld a kategóriára (nem objektumot), 
   * ezért itt határozzuk meg az árat a kategória nevéből
   */
  getTicketPrice(ticket: Ticket): number {
    // Teljes jegyobjektum kilogolása a hibakeresés támogatásához
    console.log('TELJES JEGYOBJEKTUM:', JSON.stringify(ticket, null, 2));

    // Részletes kategória és ár logolás
    console.log('Jegy ár kiszámítása:', {
      jegyId: ticket._id,
      kategoria: ticket.category,
      kategoriaType: typeof ticket.category,
      kategoriaName: typeof ticket.category === 'object' ? 
        (ticket.category as any).name : 
        (typeof ticket.category === 'string' ? ticket.category : 'ismeretlen')
    });

    // 1. Vizsgáljuk meg, hogy a kategoria objektum-e és van-e price tulajdonsága
    if (ticket.category && typeof ticket.category === 'object') {
      // Ha a ticket.category egy objektum, próbáljuk meg kiloggolni a tartalmát
      console.log('Kategória objektum tulajdonságai:', Object.keys(ticket.category));

      // Ha van price tulajdonság, azt használjuk
      if ('price' in ticket.category && typeof ticket.category.price === 'number') {
        console.log('Talált ár a price tulajdonságban:', ticket.category.price);
        return ticket.category.price;
      }

      // Ha nincs price tulajdonság, akkor keressük egyebeket is
      const categoryObj = ticket.category as any;

      // Nézzük meg az összes tulajdonságot és értéket
      for (const key in categoryObj) {
        console.log(`Kategória tulajdonság: ${key} = ${categoryObj[key]} (${typeof categoryObj[key]})`);
      }

      // Ellenőrizzük különböző lehetséges ár tulajdonságokat
      if (typeof categoryObj.price === 'number') {
        console.log('Talált ár price tulajdonságban:', categoryObj.price);
        return categoryObj.price;
      } else if (typeof categoryObj.amount === 'number') {
        console.log('Talált ár amount tulajdonságban:', categoryObj.amount);
        return categoryObj.amount;
      } else if (typeof categoryObj.cost === 'number') {
        console.log('Talált ár cost tulajdonságban:', categoryObj.cost);
        return categoryObj.cost;
      } else if (typeof categoryObj.value === 'number') {
        console.log('Talált ár value tulajdonságban:', categoryObj.value);
        return categoryObj.value;
      }

      // Ha a kategoria neve ismert, próbáljunk hardkódolt értékeket (ideiglenes megoldás)
      if (categoryObj.name === 'Terasz') {
        console.log('Hardkódolt érték "Terasz" kategóriához:', 25000);
        return 25000;
      } else if (categoryObj.name === 'Álló') {
        console.log('Hardkódolt érték "Álló" kategóriához:', 10000);
        return 10000;
      } else if (categoryObj.name === 'VIP') {
        console.log('Hardkódolt érték "VIP" kategóriához:', 15000);
        return 15000;
      }
    }

    // Ha a kategória egy string, akkor nézzük a kategória neveket
    if (typeof ticket.category === 'string') {
      const categoryName = ticket.category;
      console.log('Kategória név alapján ár kiszámítása:', categoryName);

      // Terasz kategóriához kézzel beállítjuk az árat
      if (categoryName === 'Terasz') {
        console.log('Terasz kategóriához fix ár:', 25000);
        return 25000;
      } else if (categoryName === 'Álló') {
        console.log('Álló kategóriához fix ár:', 10000);
        return 10000;
      } else if (categoryName === 'VIP') {
        console.log('VIP kategóriához fix ár:', 15000);
        return 15000;
      }
    }

    // Ha semmi más nem működik, adjunk egy alapértelmezett értéket
    console.warn('Nem sikerült árat meghatározni a jegyhez, alapértelmezett érték:20000');
    return 20000;
  }

  cancelTicket(ticketId: string): void {
    if (confirm('Biztosan lemondód ezt a jegyet?')) {
      this.ticketService.cancelTicket(ticketId).subscribe({
        next: () => {
          this.loadTickets();
        },
        error: (err: any) => {
          this.error = 'Hiba történt a jegy lemondása közben. Kérjük próbálja újra később.';
          console.error('Hiba a jegy lemondása közben:', err);
        }
      });
    }
  }
}
