import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable, catchError, throwError } from 'rxjs';
import { environment } from '../../environments/environment';
import { Event, TicketCategory } from '../models/event.model';

@Injectable({
  providedIn: 'root'
})
export class EventService {
  private apiUrl = `${environment.apiUrl}/events`;

  constructor(private http: HttpClient) {}

  getEvents(): Observable<Event[]> {
    return this.http.get<Event[]>(this.apiUrl);
  }

  getEvent(id: string): Observable<Event> {
    return this.http.get<Event>(`${this.apiUrl}/${id}`);
  }

  /**
   * Új esemény létrehozása
   * Minimalista megközelítés - csak a leg
   * @param eventData Az új esemény adatai
   */
  createEvent(eventData: any): Observable<Event> {
    console.log('EventService - Minimalista createEvent kérés indul', eventData);

    // TELJESEN új megközelítés - Minimalist adatok
    // Csak a legfontosabb adatokat küljük, a lehető legegyszerűbb formában
    const simpleEventData = {
      title: eventData.title,
      description: eventData.description,
      date: new Date(eventData.date).toISOString(),
      location: eventData.location,
      ticketCategories: [
        {
          name: eventData.ticketCategories && eventData.ticketCategories.length > 0 ? 
                eventData.ticketCategories[0].name : 'Alap jegy',
          price: eventData.ticketCategories && eventData.ticketCategories.length > 0 ? 
                 Number(eventData.ticketCategories[0].price) : 1000,
          availableTickets: eventData.ticketCategories && eventData.ticketCategories.length > 0 ? 
                            Number(eventData.ticketCategories[0].availableTickets) : 100
        }
      ]
    };

    // 1) Változat - Alap objektum küldése
    console.log('Küldés 1. formátumban (egyszerű objektum):', simpleEventData);
    return this.http.post<Event>(this.apiUrl, simpleEventData).pipe(
      catchError(error => {
        console.error('1. formátum sikertelen:', error);

        // 2) Változat - Form adat formátumban küldés
        console.log('Próbálkozás 2. formátumban (FormData)');
        const formData = new FormData();
        formData.append('title', eventData.title);
        formData.append('description', eventData.description);
        formData.append('date', new Date(eventData.date).toISOString());
        formData.append('location', eventData.location);
        formData.append('ticketCategoryName', 'Alap jegy');
        formData.append('ticketCategoryPrice', '1000');
        formData.append('ticketCategoryAvailableTickets', '100');

        return this.http.post<Event>(this.apiUrl, formData).pipe(
          catchError(error2 => {
            console.error('2. formátum sikertelen:', error2);

            // 3) Változat - Query paraméterek
            console.log('Próbálkozás 3. formátumban (query params)');
            const params = new HttpParams()
              .set('title', eventData.title)
              .set('description', eventData.description)
              .set('date', new Date(eventData.date).toISOString())
              .set('location', eventData.location);

            return this.http.post<Event>(this.apiUrl, null, { params }).pipe(
              catchError(error3 => {
                console.error('Minden formátum sikertelen:', error3);
                return throwError(() => error3);
              })
            );
          })
        );
      })
    );
  }

  updateEvent(id: string, event: Event): Observable<Event> {
    return this.http.put<Event>(`${this.apiUrl}/${id}`, event);
  }

  deleteEvent(id: string): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }

  getOrganizerEvents(): Observable<Event[]> {
    return this.http.get<Event[]>(`${environment.apiUrl}/api/users/my-events`);
  }
}
