import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, catchError, throwError } from 'rxjs';
import { environment } from '../../environments/environment';
import { Ticket } from '../models/ticket.model';

@Injectable({
  providedIn: 'root'
})
export class TicketService {
  // Feltételezzük, hogy az environment.apiUrl már tartalmazza az '/api' előtagot
  // Közvetlenül használjuk az environment.apiUrl-t a következetesség érdekében

  constructor(private http: HttpClient) {}

  /**
   * Jegy vásárlása
   * @param eventId Esemény azonosító
   * @param categoryId A jegy kategória azonosítója
   * @param quantity Megvásárolni kívánt jegyek száma
   */
  purchaseTicket(eventId: string, categoryId: string, quantity: number): Observable<Ticket> {
    // A második hiba: "Invalid ticket category" arra utal, hogy a kategoria értéke nem érvényes.
    // Próbáljunk meg különböző formátumokat
    const endpoint = `${environment.apiUrl}/tickets`;

    // Próbáljuk meg, hogy a backend talán egy numerikus ID-t vár ObjectId helyett
    // Láthatjuk, hogy a hibajelzések szerint a category mező talán más típust vár
    const numericId = categoryId.substring(categoryId.length - 4); // csak az utolsó 4 karaktert használjuk id-ként

    // Többféle formátumot próbálunk meg
    const requestPayload = {
      eventId: eventId,
      category: categoryId,  // Ez az eredeti ObjectID format
      // Próbáljuk azt is, hogy talán a backend egy 'categoryId' mezőt vár és nem 'category'-t
      categoryId: categoryId,
      // Talán a backend a kategória ID-t más formátumban várja (pl nem MongoDB ObjectId)
      categoryValue: numericId,
      // Talán a quantity értékét stringként várja
      quantity: Number(quantity)
    };

    console.log('Jegyvásárlási kérés (újabb próbálkozás):', {
      endpoint,
      method: 'POST',
      payload: requestPayload
    });

    // Részletes hibalogolással figyeljük a HTTP hibát
    return this.http.post<Ticket>(endpoint, requestPayload)
      .pipe(
        catchError((error: HttpErrorResponse) => {
          console.error('Részletes vásárlási hiba:', {
            status: error.status,
            message: error.message,
            errorBody: error.error
          });

          if (error.error && error.error.errors && Array.isArray(error.error.errors)) {
            console.log('A szerver által jelentett hibák:');
            error.error.errors.forEach((err: any, index: number) => {
              console.log(`Hiba #${index + 1}:`, err);
            });
          }

          return throwError(() => error);
        })
      );
  }

  getUserTickets(): Observable<Ticket[]> {
    // Alternatív végpontokat próbálunk, mert az eredeti 500-as hibát ad
    // 1. Végpont: /api/tickets/my-tickets
    const endpoint = `${environment.apiUrl}/tickets/my-tickets`;
    console.log('Felhasználói jegyek lekérése:', endpoint);

    return this.http.get<Ticket[]>(endpoint);
  }

  cancelTicket(ticketId: string): Observable<void> {
    const endpoint = `${environment.apiUrl}/tickets/cancel/${ticketId}`;
    console.log('Jegy lemondása:', endpoint);
    // Alternatív formátumban próbáljuk a lemondást
    return this.http.post<void>(endpoint, {
      ticketId: ticketId
    });
  }

  getEventTickets(eventId: string): Observable<Ticket[]> {
    const endpoint = `${environment.apiUrl}/tickets/event/${eventId}`;
    console.log('Eseményjegyek lekérése:', endpoint);
    return this.http.get<Ticket[]>(endpoint);
  }
}
