import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { Ticket } from '../models/ticket.model';

@Injectable({
  providedIn: 'root'
})
export class TicketService {
  // Feltételezzük, hogy az environment.apiUrl már tartalmazza az '/api' előtagot
  // Közvetlenül használjuk az environment.apiUrl-t a következetesség érdekében

  constructor(private http: HttpClient) {}

  /**
   * Jegy vásárlása
   * @param eventId Esemény azonosító
   * @param categoryId A jegy kategória azonosítója
   * @param quantity Megvásárolni kívánt jegyek száma
   */
  purchaseTicket(eventId: string, categoryId: string, quantity: number): Observable<Ticket> {
    // Vizsgáljuk meg, pontosan mit küldünk a backend API-nak
    const endpoint = `${environment.apiUrl}/tickets`;
    const requestPayload = {
      eventId: eventId,
      categoryId: categoryId,
      quantity: quantity
    };

    console.log('Jegyvásárlási kérés:', {
      endpoint,
      method: 'POST',
      payload: requestPayload
    });

    return this.http.post<Ticket>(endpoint, requestPayload);
  }

  getUserTickets(): Observable<Ticket[]> {
    // Alternatív végpontokat próbálunk, mert az eredeti 500-as hibát ad
    // 1. Végpont: /api/tickets/my-tickets
    const endpoint = `${environment.apiUrl}/tickets/my-tickets`;
    console.log('Felhasználói jegyek lekérése:', endpoint);

    return this.http.get<Ticket[]>(endpoint);
  }

  cancelTicket(ticketId: string): Observable<void> {
    const endpoint = `${environment.apiUrl}/tickets/cancel/${ticketId}`;
    console.log('Jegy lemondása:', endpoint);
    // Alternatív formátumban próbáljuk a lemondást
    return this.http.post<void>(endpoint, {
      ticketId: ticketId
    });
  }

  getEventTickets(eventId: string): Observable<Ticket[]> {
    const endpoint = `${environment.apiUrl}/tickets/event/${eventId}`;
    console.log('Eseményjegyek lekérése:', endpoint);
    return this.http.get<Ticket[]>(endpoint);
  }
}
